var requestMiddleware = {
  all: [],
  post: [],
  put: [],
  get: [],
  delete: [],
  head: []
};

function configureControllerRequest (controller) {
  Object.keys(requestMiddleware).forEach(function (verb) {
    controller[verb](basePath, requestMiddleware[verb]);
  });
}

function request (controller, verbs, middleware) {
  if (middleware === undefined) {
    middleware = verbs;
    verbs = 'all';
  }

  if (typeof verbs === 'string') verbs = verbs.split(' ');

  verbs.forEach(function (verb) {
    requestMiddleware[verb].push(middleware);
  });

}
